mapper.select:
select 'false' as QUERYID,

from schedule_task
where intid = #{intid,jdbcType=INTEGER}

select 'false' as QUERYID,

from schedule_task
where province = #{province,jdbcType=VARCHAR}
      and netype=#{netype,jdbcType=VARCHAR}
      and vendor=#{vendor,jdbcType=VARCHAR}
      and version=#{version,jdbcType=VARCHAR}
      and datatype=#{datatype,jdbcType=VARCHAR}
      and datetime=#{datetime,jdbcType=VARCHAR}

select 'false' as QUERYID,

from schedule_task
where executetime <= #{executetime,jdbcType=INTEGER}
and taskstatus= #{taskstatus,jdbcType=VARCHAR}

mapper.insert:
insert into schedule_task (province, netype,
  vendor, version, datatype, 
  datetime, executetime, taskstatus
  )
values (#{province,jdbcType=VARCHAR}, #{netype,jdbcType=VARCHAR},
  #{vendor,jdbcType=VARCHAR}, #{version,jdbcType=VARCHAR}, #{datatype,jdbcType=VARCHAR}, 
  #{datetime,jdbcType=VARCHAR}, #{executetime,jdbcType=INTEGER}, #{taskstatus,jdbcType=VARCHAR}
  )
