mapper.select:
select 'false' as QUERYID,

from task_detailstatus
where subtask = #{subtask,jdbcType=VARCHAR}
and province = #{province,jdbcType=VARCHAR}
and netype=#{netype,jdbcType=VARCHAR}
and vendor=#{vendor,jdbcType=VARCHAR}
and version=#{version,jdbcType=VARCHAR}
and datatype=#{datatype,jdbcType=VARCHAR}
and datetime=#{datetime,jdbcType=VARCHAR}

mapper.update:
update task_detailstatus
set  taskstatus = #{taskstatus,jdbcType=VARCHAR},
     commtent = #{commtent,jdbcType=VARCHAR}
where taskid = #{taskid,jdbcType=INTEGER}
    and subtask = #{subtask,jdbcType=VARCHAR}
