mapper.select:
select 'false' as QUERYID,

from udp_task
where intid = #{intid,jdbcType=INTEGER}

select 'false' as QUERYID,

from udp_task
where  province = #{province,jdbcType=VARCHAR}
    and  netype = #{netype,jdbcType=VARCHAR}
    and vendor = #{vendor,jdbcType=VARCHAR}
    and version = #{version,jdbcType=VARCHAR}
    and datetime = #{datetime,jdbcType=VARCHAR}
    and type = #{type,jdbcType=VARCHAR}

mapper.insert:
insert into udp_task (province, netype,
  vendor, version, datetime,
  type, taskstatus, failurereason
  )
values (#{province,jdbcType=VARCHAR}, #{netype,jdbcType=VARCHAR},
  #{vendor,jdbcType=VARCHAR}, #{version,jdbcType=VARCHAR}, #{datetime,jdbcType=VARCHAR},
  #{type,jdbcType=VARCHAR}, #{taskstatus,jdbcType=VARCHAR}, #{failurereason,jdbcType=VARCHAR}
  )
